			{
				if (firstPhase)
				{
					if (pi_rowHeight != rowHeight)
					{
						rowHeight = pi_rowHeight;
						return VirtualRepeatEndPlaceItemResult::TotalSizeUpdated;
					}
				}
				return VirtualRepeatEndPlaceItemResult::None;
			}

