	{
		if (!remote->connectionForcedToStop)
		{
			bool cancel = false;
			for (auto l : listeners)
			{
				l->BeforeClosing(cancel);
				if (cancel) return;
			}
			for (auto l : listeners) l->AfterClosing();
		}
		remote->DestroyNativeWindow(this);
	}

